from platformio.exception import PlatformioException
from click.exceptions import ClickException


class CommandError(PlatformioException):
    STACK_LEVEL = 1


# common
# =======================================================================

class UnknownError(CommandError):
    """
    {0} Name of the function where the error occurred
    {1} Error Messages
    """
    MESSAGE = "{0}"


class PlatformioJSONDecodeError(CommandError):
    """
    {0} :Character string to be parsed
    """
    MESSAGE = "{0} parsing error!"


class PlatformioFileNotFound(ClickException,CommandError):
    STACK_LEVEL = 2
    MESSAGE = "{0} not found"


class PlatformioLowLevelError(CommandError):
    """
    ValueError, KeyError, IndexError
    {0} Error Messages{1} Error Messages
    """
    MESSAGE = "{0}"


class NetWorkConnectError(CommandError):
    MESSAGE = "{0}"


