
class BaseParser:
    def __init__(self):
        self._pending_buffer = bytes()

    @property
    def pending_data(self):
        return self._pending_buffer

    def parse_chunk(self, chunk):
        if chunk:
            self._pending_buffer += chunk
            return self._parse_pending()
        return None

    def _parse_pending(self):
        raise NotImplementedError()
