import re
import click


def replace(pattern, target, data):
    capture_list = re.findall(pattern, data)
    if capture_list:
        data = re.sub(pattern, target, data)
        click.echo(
            f'\tReplace: {click.style(capture_list[0])} --> {click.style(target, bold=True, fg="blue")}'
        )
    return data
